import javafx.application.Application;import javafx.scene.Scene; import javafx.stage.Stage; import javafx.scene.layout.Pane; import javafx.scene.layout.VBox; public class Driver extends Application{		public static void main(String[] args){		launch(args);	}		public void start(Stage stage){		stage.setMinHeight(800);		VBox panel = new VBox();		Gui gui = new Gui();		panel.getChildren().add(gui);		Scene scene = new Scene(panel);		stage.setScene(scene);		stage.setTitle("project");		stage.show();	}	}